```javascript
function copyRangeToAnotherSheet() {
  var config = getConfig();
  var ss = SpreadsheetApp.getActiveSpreadsheet();
  var sourceSheet = ss.getSheetByName(config.sourceSheetName);
  var targetSheet = ss.getSheetByName(config.targetSheetName);

  // Define the source range based on the configuration
  var sourceRangeString = config.sourceRangeStart + ':' + config.sourceRangeEndColumn + config.rowsToCheck;
  var sourceRange = sourceSheet.getRange(sourceRangeString);
  var numRows = sourceRange.getNumRows();
  var numCols = sourceRange.getNumColumns();

  // Trim whitespaces from source data
  var sourceData = trimData(sourceRange.getValues());

  var duplicatedRows = [];
  var notDuplicatedRows = [];

  for (var i = 0; i < sourceData.length; i++) {
    var rowData = [sourceData[i]];

    if (isRowEmpty(rowData, config.columnsToCompare)) {
      continue;
    }

    if (isDuplicate(rowData, targetSheet, config.columnsToCompare)) {
      duplicatedRows.push(i + 1);
    } else {
      notDuplicatedRows.push(i + 1);
      // Get the Range object for the target range start
      var targetRangeStart = targetSheet.getRange(config.targetRangeStart);

      // Insert a new row at the specified position in the target sheet
      targetSheet.insertRows(targetRangeStart.getRow(), 1);

      // Get the target range having the same dimensions as the source range
      var targetRange = targetSheet.getRange(targetRangeStart.getRow(), targetRangeStart.getColumn(), 1, numCols);

      // Copy trimmed data to the target range
      targetRange.setValues(rowData);
    }
  }

  // ... rest of the function remains the same
}

function isRowEmpty(rowData, columnsToCompare) {
  for (var i = 0; i < columnsToCompare.length; i++) {
    var colIndex = columnsToCompare[i] - 1;
    if (rowData[0][colIndex] !== '') {
      return false;
    }
  }
  return true;
}
